package com.cg;

public class FindingTheLength {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "String";
		int n  = s.length();
		int count=0;
		for(int i=0;i<n;i++) {
			count++;
		}
		
System.out.println("The length of the String is : " + count);
	}

}
