package com.cg;

public class VowelOrConsonant {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = 'c';

		if(c=='a'||c=='e'||c=='i'||c=='o'||c=='u') {
			System.out.println(c+"It is a vowel");
		}
		else {
			System.out.println(c + " It is a consonant");
		}
	}

}
